document.addEventListener("DOMContentLoaded",()=>{listenerProjects(),listenerSkills()});const listenerProjects=()=>{const e=document.querySelector(".grid--projects");new IntersectionObserver(callbackObserverProjects,{}).observe(e)},callbackObserverProjects=(e,s)=>{const r=document.querySelectorAll(".card");e.forEach(e=>{e.isIntersecting?r.forEach(e=>{e.classList.add("swipe-up")}):r.forEach(e=>{e.classList.remove("swipe-up")})})},listenerSkills=()=>{const e=document.querySelector(".bg--primary");new IntersectionObserver(callbackObserverSkills,{}).observe(e)},callbackObserverSkills=(e,s)=>{const r=document.querySelectorAll(".grid--skills");e.forEach(e=>{e.isIntersecting?r.forEach(e=>{e.classList.add("swipe-down")}):r.forEach(e=>{e.classList.remove("swipe-down")})})};